.video {
	position: relative;
	overflow: hidden;
	background-size: cover;
	background-position: center;
	height: 700px;
}

.video .container {
	position: absolute;
	height: 100%;
	width: 100%;
	max-width: 100%;
	z-index: 4;
	margin: 0 auto;
	left: 0;
	right: 0;
}

.video .row {
	height: 100%;
}

.video .overlay {
	background: linear-gradient(180deg, rgba(0, 0, 0, 0.31) 0%, rgba(0, 0, 0, 0) 100%);
	z-index: 3;
	transform: rotate(180deg);
}

.video .image {
	position: absolute;
	z-index: 1;
	top: 0;
	left: 0;
	height: 100%;
	width: 100%;
	background-size: cover;
	background-position: center;
}

.video video {
	position: absolute;
	z-index: 2;
	left: 0;
	top: 0;
	min-width: 105%;
    min-height: 100%;
}

.video .icon {
	height: 150px;
	width: 150px;
	margin-left: auto;
	margin-right: auto;
	display: block;
	background: rgba(255,255,255,0.7);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: 0.5s all;
}

.video .icon:hover {
	height: 160px;
	width: 160px;
}

.video .icon img {
	height: 125px;
	width: 125px;
}

.bg-video .video {
    height: 100%;
    width: calc(100% + 12px);
    position: absolute;
    top: 0;
    left: -12px;
    z-index: 1;
    overflow: hidden;
    margin: 0;
}

.bg-video .video video {
    right: 0;
    bottom: 0;
    width: auto;
    height: auto;
    position: absolute;
	z-index: 2;
	left: 0;
	top: 0;
	min-width: 105%;
    min-height: 100%;
}

.bg-video .overlay {
	z-index: 2;
}

/* YouTube Embeds */

.video-container { 
	position: relative; 
	padding-bottom: 56.25%; 
	padding-top: 30px; 
	height: 0;
	overflow: hidden; 
}

.video-container iframe, .video-container object, .video-container embed { 
	position: absolute; 
	top: 0; 
	left: 0; 
	width: 100%; 
	height: 100%; 
}

/* Images */

img {
	max-width: 100%;
}

.page-template-careers .icon {
	height: 50px;
	width: 50px;
}

/* Google Maps */

#map-container {
	width: 100%;
	height: 100%;
}
 
#map {
	height: 100%;
	min-height: 600px;
	width: 100%;
}

.gm-style .gm-style-iw-c {
	background: #102342;
	width: auto;
	padding-right: 3em !important;
}

.gm-style .gm-style-iw-t::after {
	background: linear-gradient(45deg,#102342 50%,rgba(255,255,255,0) 51%,rgba(255,255,255,0) 100%);
}

.gm-style .gm-style-iw-d {
	font-size: 1em;
	padding: 1em;
	margin: 0;
	overflow: hidden !important;
}
 
.gm-style .gm-style-iw-c {
	border-radius: 0;
	padding: 0;
}
 
.gm-style .gm-style-iw-c h3 {
	font-size: 1.6em;
	margin-bottom: 1em;
}

.gm-style .col-md-auto, .gm-style .col {
	font-size: 1.2em;
}

.gm-style .col-md-auto + .col {
	border-left: 1px solid #ffffff;
}
 
.gm-style strong {
 	font-weight: 500;
 	font-size: 1.3em;
}

.gm-style .gm-style-iw-c button {
	top: 1em !important;
	right: 1em !important;
	margin: 0 !important;
	filter: invert(100%) sepia(3%) saturate(0%) hue-rotate(229deg) brightness(103%) contrast(102%);
}
 
.gm-style .gm-style-iw-c button:focus {
	outline: unset;
}

.gm-style .gm-style-iw-c button img {
	height: 100% !important;
	width: 100% !important;
	margin: 0 !important;
	filter: invert(85%) sepia(100%) saturate(0%) hue-rotate(149deg) brightness(105%) contrast(102%);
}

.location {
	cursor: pointer;
	color: #102342;
}

/* Header */

header.container-fluid {
	position: fixed;
	z-index: 999;
	top: 0;
	left: 0;
	right: 0;
	width: 100%;
	margin-top: 0;
	margin-bottom: 0;
	height: 110px;
	background: #ffffff;
	transition: 0.5s height;
	border-bottom: 1px solid #EDEDED;
}

.logged-in header.container-fluid {
	top: 32px;
}

header.container-fluid.smaller {
	height: 90px;
}

header .row {
	padding-top: 0;
	padding-bottom: 0;
	height: 100%;
}

header .container {
	height: 100%;
	max-width: 2000px;
	width: 95%;
}

header .separator {
	height: 60%;
	background: #102342;
	width: 1px;
	display: block;
}

#logo img {
	width: 250px;
	margin: 0;
	height: auto;
	transition: 0.5s all;
}

header.smaller #logo img {
	width: 225px;
}

/* Header Navigation */

#navigation {
	position: relative;
	z-index: 2;
	height: 100%;
}

.menu-primary-navigation-container {
	height: 100%;
}

#menu-primary-navigation {
	margin: 0;
	padding: 0;
	height: 100%;
	display: flex;
	align-items: center;
}

#menu-primary-navigation li {
	list-style-type: none;
	text-transform: none;
	margin: 0;
	font-size: 1em;
	width: auto;
	height: 100%;
	display: flex;
	align-items: center;
	font-weight: 500;
	padding: 0.5em 1em;
}

#menu-primary-navigation li:before {
	display: none;
}

#menu-primary-navigation li:first-child {
	padding-left: 0;
}

#menu-primary-navigation a {
	color: #102342;
	font-family: 'Montserrat';
	font-size: 1em;
	transition: 0.5s color;
	text-decoration: none;
	z-index: 2;
	position: relative;
	justify-content: center;
	height: 100%;
	display: flex;
	align-items: center;
}

#menu-primary-navigation a:hover {
	color: #F15A24;
}

#menu-primary-navigation .menu-item-has-children > a:after {
	content: '';
	background-size: cover;
	background-image: url(/wp-content/themes/nac-coal/images/icon-dropdown.svg);
	height: 8px;
	width: 15px;
	margin-left: 0.5em;
	display: inline-block;
	position: relative;
	top: -2px;
}

#menu-primary-navigation .menu-item-has-children > a {
	cursor: default;
}

#menu-primary-navigation li a:before {
	content: '';
	height: 2px;
	background: #F15A24;
	width: 0;
	transition: 0.5s all;
	position: absolute;
	left: 0;
	bottom: 1.5em;
}

header.smaller #menu-primary-navigation li a:before {
	bottom: 1em;
}

#menu-primary-navigation li:hover > a:before {
	width: 100%;
}

#menu-primary-navigation .sub-menu {
	position: absolute;
    opacity: 0;
    pointer-events: none;
    transition: 0.5s opacity;
    z-index: 1;
    padding-left: 0;
    padding: 1em;
    width: auto;
    left: 0;
    background: #F9F9F9;
    overflow: hidden;
    margin: 0;
    box-shadow: 0px 4px 16px rgba(16, 35, 66, 0.1);
    top: 20px;
    margin-bottom: 30px;
}
 

#menu-primary-navigation li:first-child .sub-menu {
	left: -1em;
}

#menu-primary-navigation .menu-item-has-children:hover .sub-menu {
	opacity: 1;
	pointer-events: auto;
}

#menu-primary-navigation .sub-menu li {
	display: block;
	padding: 0;
	white-space: nowrap;
	width: 100%;
	height: auto;
	padding: 0.25em;
}

#menu-primary-navigation .sub-menu li a {
	display: inline-block;
}

header.smaller #menu-primary-navigation .sub-menu li a:before, #menu-primary-navigation .sub-menu li a:before {
	bottom: 0;
}

#nav-icon {
	position: relative;
	display: flex;
	align-items: center;
	height: 100%;
	justify-content: flex-end;
  	width: 25px;
}

.nav-icon {
  	width: 100%
 }

.nav-icon:after, 
.nav-icon:before, 
.nav-icon div {
  	background-color: #102342;
  	content: '';
  	display: block;
  	height: 1px;
  	margin: 6px 0;
  	border-radius: 3px;
  	transition: all .2s ease-in-out;
  	margin-left: auto;
  	border: 1px solid #102342;
}

.nav-icon:before {
	width: 100%;
}

.nav-icon div {
	width: 60%;
}

.nav-icon:after {
	width: 100%;
}

.nav-icon.close:before {
  	transform: translateY(7px) rotate(135deg);
  	width: 100%;
}

.nav-icon.close:after {
  	transform: translateY(-9px) rotate(-135deg);
}

.nav-icon.close div {
  	transform: scale(0);
}

.close:not(:disabled):not(.disabled):focus, .close:not(:disabled):not(.disabled):hover, .nav-icon.close {
	opacity: 1;
}

/* Special Layouts */

#header {
	margin-top: 110px;
	height: 600px;
	overflow: hidden;
}

.page-template-home #header {
	height: calc(100vh - 110px);
}

.page-template-home.logged-in #header {
	height: calc(100vh - 110px - 32px);
}

#header.h-full {
	height: calc(100vh - 110px);
}

#header.h-auto {
	height: auto;
}

#header .container {
	position: relative;
	height: 100%;
}

#header .row {
	height: 100%;
	padding-top: 6em;
	padding-bottom: 6em;
}

#down-arrow {
    position: absolute;
    display: block;
    bottom: 6em;
    right: -7%;
}

#down-arrow img {
	position: relative;
	top: 0;
	transition: 0.5s all;
}

#down-arrow:hover img {
    top: 10px;
}

.image-right, .image-left {
	overflow: hidden;
	display: flex;
	align-items: center;
	padding: 0;
}

.image-left .image, .image-right .image {
	position: absolute;
	padding: 0;
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
}

.image-left .image .slide-in, .image-right .image .slide-in {
	position: absolute;
	top: 0;
	z-index: 1;
}

.image-left .image img.negative-left, .image-right .image img.negative-left {
	position: relative;
	z-index: 2;
	height: 80%;
}

.image-right .image img.negative-left {
	left: -30%;
}

.image-left .image.slide-in > div, .image-right .image.slide-in > div, .image-right .image .slide-in > div, .image-left .image .slide-in > div {
	background-size: cover;
	background-position: center;
	height: 100%;
	width: 100%;
}

.image-left .image {
	left: 0;
}

.image-right .image {
	right: 0;
}

.image-right.position-relative .image, .image-left.position-relative .image {
	position: relative;
	z-index: 2;
}

.image-right.position-relative .container, .image-left.position-relative .container {
	position: absolute;
	height: 100%;
	left: 0;
	right: 0;
	margin: 0 auto;
	z-index: 1;
}

.image-left + .testimonials, .image-right + .testimonials,
.image-left + .video-section, .image-right + .video-section,
.image-left + .numbered-list, .image-right + .numbered-list,
.image-left + .contact, .image-right + .contact {
	margin-top: 12em;
}

.overlay {
	position: absolute;
	height: 100%;
	width: 100%;
	top: 0;
	left: 0;
	z-index: 1;
	background: linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.44) 100%);
	pointer-events: none;
}

.bottom {
	bottom: 0;
}

.right {
	right: 0;
}

.underline {
	position: absolute;
	height: 85px;
	width: 1450px;
	z-index: -1;
}

.underline.smaller {
	height: 57px;
    width: 950px;
}

.underline.full-width {
