	height: 115px;
}

.row .underline {
	width: 100%;
	height: 57px;
	top: 40px;
}

.bg-video .underline {
	z-index: 3;
}

.underline.orange {
	mix-blend-mode: multiply;
	opacity: 0.95;
}

.underline.gray {
	opacity: 0.1;
}

.underline.bottom {
	bottom: 7.5em;
}

.underline.bottom.smaller {
	bottom: 4.5em;
}

.underline.negative-right {
