}

.underline.negative-left {
	left: -7em;
}

.underline-container {
	position: relative;
	height: 100%;
	width: 100%;
	overflow: hidden;
}

.underline .line {
	background-size: cover;
	height: 100%;
	width: 100%;
	position: absolute;
	bottom: -100%;
	left: -56%;
