}

.underline.gray .line {
	background-image: url(/wp-content/themes/nac-coal/images/underline-light-gray.svg);
}

.grid {
	display: grid !important;
	grid-gap: 1em 1em;
}

.container-fluid.grid {
	padding: 0;
}

.grid .box {
	background: #F15A24;
	color: #ffffff;
	padding: 1em;
	width: auto;
	position: absolute;
	font-size: 0.9em;
	opacity: 0;
	transition: 0.5s all;
}

.grid > div:hover .box {
	opacity: 1;
}

.grid.two-one, .grid.one-two {
	grid-template-columns: 1fr 1fr 1fr;
	grid-template-rows: 1fr 1fr;
}

.grid.two-one .item-1 {
	grid-area: 1 / 1 / 2 / 3;
	background-size: cover;
	background-position: center;
	height: 400px;
	position: relative;
}

.grid.two-one .item-2 {
	grid-area: 1 / 3 / 2 / 4;
	background-size: cover;
