	position: relative;
}

.grid.two-one .item-3 {
	grid-area: 2 / 1 / 3 / 4;
	background-size: cover;
	background-position: center;
	height: 400px;
	position: relative;
}

.grid.two-one .box {
	bottom: 0;
	left: 0;
}

.grid.two {
	grid-template-columns: 1fr 1fr 1fr;
	grid-template-rows: 1fr;
}

.grid.two .item-1 {
	grid-area: 1 / 1 / 2 / 3;
