.history.bg-image {
	overflow: visible;
	z-index: 3;
	padding-left: 0;
	padding-right: 0;
}

.history .box {
	position: absolute;
	bottom: -3em;
	right: 0;
	padding-left: 0;
	padding-right: 0;
