/* Cards */

.card {
	justify-content: center;
	border: 0;
	overflow: hidden;
	border-radius: 0;
	background: transparent;
}

a.card:hover {
	text-decoration: none;
}

.card-body {
	padding-left: 0;
	padding-right: 0;
	color: #7C7B7A;
}

.card-body .small {
	color: #9E9B99;
}

a.card:hover .btn-text img {
	left: 5px;
}

.card.grid-item .card-footer {
	background: #0060CC;
	padding: 1em;
	border-radius: 0;
}

.card.grid-item .card-footer, .card.grid-item .card-footer h4, .card.grid-item .card-footer .btn-text {
	color: #ffffff;
}

.card.bg-image {
	height: auto;
}

.card.bg-image .card-body {
	opacity: 0;
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: rgba(241, 90, 36, 0.88);
	transition: 0.5s all;
	padding: 2em;
}

.card.bg-image:hover .card-body {
	opacity: 1;
}

.card.slide-up {
	height: 500px;
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
	justify-content: center;
}

.card.slide-up.bottom {
	height: 100%;
}

